mapper.update:
update dynamic
set content = #{dynamic.content}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set url1 = #{dynamic.url1}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set url2 = #{dynamic.url2}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set url3 = #{dynamic.url3}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set avatar_url = #{dynamic.avatarUrl}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set name = #{dynamic.name}
where account = #{dynamic.account} and time = #{dynamic.time};

update dynamic
set thumb = thumb + 1
where account = #{dynamic.account} and time = #{dynamic.time};

mapper.select:
select account, avatar_url, name ,content, time, url1, url2, URL3, thumb
from dynamic
order by time desc;

select dy_id
from dynamic
where account = #{dynamic.account} and time = #{dynamic.time};
